import React, { Component } from 'react';
import Carrito from './carrito';

let parseo=[];
let carrito=[];
let productos=[{
    codigo:12345678,
    idproducto:23,
    nom_producto:"Aceite Primor",
    precio:5.60,
    stock:20
},{
    codigo:87654321,
    idproducto:17,
    nom_producto:"Sapolio",
    precio:2.50,
    stock:50
}];

class BuscarProducto extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listaDeProductos: [],
            listadeCompras:[]
        }
    }
    buscarProducto=(e)=>{
        var codigo=document.getElementById("codigo").value;
        for(var i in productos){
            var cod=productos[i]["codigo"];
            if(codigo.length==8){
                if(cod==codigo){
                    var data=productos[i];
                    parseo.push(data);
                    this.setState({
                        listaDeProductos: parseo
                    });     
                }
            }
        }  
        
    }

    onEditHandle(event){
        var idp=arguments[0];
        var cant=document.getElementById("c"+idp).value;
        const data={
            idproducto:idp,
            codigo:arguments[1],
            nom_producto:arguments[2],
            precio:arguments[3],
            cantidad:cant
        }  
        if(carrito.length==0){
            carrito.push(data);
            console.log("Este es el carrito:"+carrito);
            parseo=[];
                this.setState({
                    listaDeCompras:carrito,
                    listaDeProductos:parseo
            })
        }else{
            for(var i in carrito){
                var idp=carrito[i]["idproducto"];
                if(data.idproducto==idp){
                    console.log("ya existe prro");
                    var cantidad=carrito[i]["cantidad"];
                    var cant=parseInt(cantidad)+parseInt(data.cantidad);
                    carrito[i]["cantidad"]=cant;
                    parseo=[];
                    this.setState({
                        listaDeCompras:carrito,
                        listaDeProductos:parseo
                    })
                }else{
                    carrito.push(data);
                    parseo=[];
                    this.setState({
                        listaDeCompras:carrito,
                        listaDeProductos:parseo
                    })
                }
            }
        }
        console.log(carrito);
    }

    render() {
        const listadeProducto= this.state.listaDeProductos.map((item,index) => {
            return(         
                    <tr key={item.idproducto}>
                            <th>{index+1}</th>
                            <th>{item.codigo}</th>
                            <th>{item.nom_producto}</th>
                            <th><input type="text" className="form-control" maxLength="5" size="4" id={"c"+item.idproducto} /></th>
                            <th>{item.precio}</th>
                            <th>{item.stock}</th>
                            <th>
                                <button className="btn btn-success" onClick={this.onEditHandle.bind(this,item.idproducto,item.codigo)}>
                                <i className="fas fa-cart-plus"></i>
                                </button>
                            </th>
                    </tr>
              
            );
        });

        const listadeCompras=this.state.listadeCompras.map((item,index)=>{
            return(
                <tr key={item.idproducto}>
                    <th>{index+1}</th>
                    <th>{item.codigo}</th>
                    <th>{item.nom_producto}</th>
                    <th><input type="text" className="form-control" maxLength="5" size="4" id={"c"+item.idproducto} /></th>
                    <th>{item.precio}</th>
                    <th>{item.stock}</th>
                    <th>
                        <button className="btn btn-success" onClick={this.onEditHandle.bind(this,item.idproducto,item.codigo)}>
                        <i className="fas fa-cart-plus"></i>
                        </button>
                    </th>
                </tr>
            );
        });
        return (
            <div className="col-12">
                <div className="card">
                    <div className="card-body">
                        <h5 className="card-title">Buscador de Productos</h5>
                        <div className="form-group">
                            <div className="row">
                                <div className="col-9">
                                    <input type="text" className="form-control" id="codigo"/>
                                </div>
                                <div className="col-3">
                                    <button type="button" className="btn btn-outline-primary btn-block" onClick={this.buscarProducto}>
                                        Buscar Productos<i className="fas fa-cart-plus"></i>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>
                <br></br>
                <div className="col-12">
                    <div className="card">
                        <div className="card-body">
                            <table className="table table-bordered">
                                <thead>
                                    <tr>
                                        <th scope="col">#</th>
                                        <th scope="col">Codigo</th>
                                        <th scope="col">Nombre</th>
                                        <th scope="col">Cantidad</th>
                                        <th scope="col">Precio</th>
                                        <th scope="col">Stock</th>
                                        <th scope="col">Agregar</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {listadeProducto}
                                </tbody>                           
                            </table>
                        </div>
                    </div>
                </div>

            </div>
        );
    }
}

export default BuscarProducto;
