import React, { Component } from 'react';
import axios from 'axios';

class BuscarCliente extends Component {

    constructor() {
        super();
        var today = new Date(),
            date =  today.getDate() + ' / ' + (today.getMonth() + 1) + ' / ' + today.getFullYear();
        this.state = {
            date : date,
            dni: '',
            clienteEncontrado: []
        }
        this.getClienteByDni = this.getClienteByDni.bind(this);
        
    }

    getClienteByDni() {
        axios.get('')
        .then(function (response) {
        
        })
        .catch(function (error) {
            console.log(error);
        })
    }



    render() {
        return (
            <div className="col-12">
                <div className="card">
                    <div className="card-body">
                        <h5 className="card-title">Datos Del Cliente</h5>
                        <div className="form-group">
                            <div className="row">
                                <div className="col-3">
                                    <input 
                                    type="text" 
                                    className="form-control" 
                                    placeholder="Dni..." 
                                    required  
                                    maxLength="8"
                                    value={this.state.dni}
                                    />
                                </div>
                                <div className="col-1">
                                    <button 
                                    type="button" 
                                    className="btn btn-outline-success btn-block"
                                    >
                                        <i className="fas fa-search"></i>
                                    </button>
                                </div>
                                <div className="col-3">
                                    <input type="text" className="form-control" placeholder="Nombres..." required disabled />
                                </div>
                                <div className="col-3">
                                    <input type="text" className="form-control" placeholder="Apellidos..." required disabled />
                                </div>
                                <div className="col-2">
                                    <input tsype="text" className="form-control" value={ this.state.date } required disabled />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default BuscarCliente;
