import React, { Component } from 'react';

let parseo=[];
let productos=[{
    codigo:12345678,
    idproducto:23,
    nom_producto:"Aceite Primor",
    precio:5.60,
    stock:20
},{
    codigo:87654321,
    idproducto:17,
    nom_producto:"Sapolio",
    precio:2.50,
    stock:50
}];

class BuscarProducto extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listaDeProductos: [],
            listaDeCompras:[]
        }
    }
    buscarProducto=(e)=>{
        var codigo=document.getElementById("codigo").value;
        for(var i in productos){
            var cod=productos[i]["codigo"];
            if(codigo.length==8){
                if(cod==codigo){
                    console.log("encontrado en la linea:"+i);
                    var data=productos[i];
                    parseo.push(data);
                    console.log(parseo);
                    this.setState({
                        listaDeProductos: parseo
                    });     
                }
            }
        }  
        
    }

    render() {
        return (
            <div className="col-12">
                <div className="card">
                    <div className="card-body">
                        <h5 className="card-title">Buscador de Productos</h5>
                        <div className="form-group">
                            <div className="row">
                                <div className="col-9">
                                    <input type="text" className="form-control" id="codigo"/>
                                </div>
                                <div className="col-3">
                                    <button type="button" className="btn btn-outline-primary btn-block" onClick={this.buscarProducto}>
                                        Buscar Productos<i className="fas fa-cart-plus"></i>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>
            </div>
        );
    }
}

export default BuscarProducto;
